var main = document.querySelector(".main");
var sum='';

arr.forEach(function (el) { 
    sum+= `
     <div class="card">
            <div class="part1">
                <img src=${el.logo} alt="">
                <button style="background-color:${el.color};">${el.save} <img class="icon" height: 15px; width: 15px;" src=${el.savelogo} alt=""></button>
            </div>
            <div class="part2">
                <h1>${el.company} <span>${el.days}</span></h1>
                <h2>${el.role}</h2>
            </div>
            <div class="part3">
                <button>${el.type1}</button>
                <button>${el.type2}</button>
            </div>
            <hr>
            <div class="part4">
                <div class="p1"> 
                <h2>${el.price}</h2>
                <p>${el.location}</p>
            </div>
            <div class="p2">
                <button>Apply now</button>
            </div>
               
            </div>
        </div>
    `;
})

main.innerHTML = sum;

var cursor = document.querySelector("#cursor");

document.body.addEventListener("mousemove", function(dets){
  cursor.style.left = dets.x + "px";
  cursor.style.top = dets.y + "px";
});

document.body.addEventListener('mousemove', function (e) {
  document.body.style.setProperty('--x', e.clientX + 'px');
  document.body.style.setProperty('--y', e.clientY + 'px');
})

